import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Products.css';
import Table from 'react-bootstrap/Table';
import { saveProducts } from '../utils/Common';
import { Link } from 'react-router-dom';
function AllProducts(props) {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [products, setProducts] = useState([]);
    const [caid, setCaid] = useState(-1)
    useEffect(() => {
        setLoading(true);
        setCaid(props.match.params.caid);
        axios({
            method: 'post',
            url: 'http://127.0.0.1:8000/api/productsbycategory/',
            data: {
              caid: props.match.params.caid
            }
          }).then(
            response => {
              //console.log(response.data)
              setProducts(response.data.products);
              console.log(response.data)
              saveProducts(response.data.products);
              setLoading(false);
            }
          ).catch(
            error => {
              setLoading(false);
              if (error.response.status === 401) setError(error.response.data.message);
              else setError("Something went wrong. Please try again later.");
            }
          )
          
    }, [])


    //console.log(products);
    //const productsArray = JSON.parse(products);
    
    if(loading) {
      return(<div>Loading...</div>)
    }
    else {
    return (
    <div>
      <Table bordered>
        <thead>
        <tr>
          <th>Product Name</th>
          <th>Material</th>
          <th>Color</th>
          <th>Stock</th>
          <th>Price</th>
        </tr>
        </thead>
        <tbody>
        {
        products.map((product) => (
        
          <tr>
          
            <td><Link to={`/product/${product.pid}`}>{product.name}</Link></td>
            <td>{product.material}</td>
            <td>{product.color}</td>
            <td>{product.stock}</td>
            <td>{product.price}</td>
          
          </tr>
          
        ))
        }
        </tbody>
      </Table>
    
     
     
    </div>
  );
  }
}
 
export default AllProducts;
