import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Products.css';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import { getProduct } from '../utils/Common';

function Product(props) {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [product, setProduct] = useState(null);
    const [comments, setComments] = useState([]);
    const [avgRating, setAvgRating] = useState(0)
    const [pid, setPid] = useState(-1)
    useEffect(() => {
        setPid(props.match.params.pid)
        setLoading(true);
        setProduct(getProduct(pid));
        console.log(getProduct(pid));
        console.log(product)
        /*
        axios({
            method: 'post',
            url: 'http://127.0.0.1:8000/api/commentandrating/',
            data: {
              pid: pid,
            }
          }).then(
            response => {
              setComments(response.data.comments);
              setAvgRating(response.data.avgRating);
              console.log(comments);
              console.log(avgRating);
            }
          ).catch(
            error => {
              
              if (error.response.status === 401) setError(error.response.data.message);
              else setError("Something went wrong. Please try again later.");
            }
          ) */
        setLoading(false);
    }, [props.match.params.pid]) 
    
    if(loading) {
        return(<div>Loading...</div>)
    }
    else {
        if(product == null) {
            return(<div>A PROBLEM OCCURED</div>)
        }
        else {
            return (
                <>
                <Container>
                <Row> 
                    <Col>
                        <img src={"http://127.0.0.1:8000"+product.image} alt = "Product's Image" width={500} height ={500}></img>
                    </Col>
                    <Col>
                    <div style={{textAlign: 'right'}}>
                        <h1>{product.name}</h1><br></br>
                        <b><p>{product.price}$</p></b><br></br>
                        <p>Color: {product.color}</p><br></br>
                        <p>Material: {product.color}</p><br></br>
                        <p>Stock: {product.color}</p><br></br>
                        <Button variant="dark">Add to Cart</Button>
                    </div>
                    </Col>
                </Row>
                <Row>
                <Col>

                </Col>
                </Row>
                </Container>
                </>
            );
        }
    }
}
        
export default Product;
