import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getUser } from '../utils/Common';
import '../css/Products.css';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';


function Cart(props) {

    const [user,setUser] = useState(getUser())
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState(props.cart)

    console.log(user)
    console.log(cart)

    useEffect(() => {
        axios({
            method: 'post',
            url: 'http://127.0.0.1:8000/api/cart/',
            data: {
              caid: props.match.params.caid
            }
          }).then(
            response => {
              //console.log(response.data)
              setProducts(response.data.products);
            }
          ).catch(
            error => {
              //if (error.response.status === 401) //setError(error.response.data.message);
              //else //setError("Something went wrong. Please try again later.");
            }
          )
      }, [])


    return (
        <div>
        <h1>{user.name} Cart</h1>

      <Grid container spacing={10}>
          {
          products.map((product) => (

            <Grid item xs={3}>

              <div class="Product-Single-Grid">

                <Link to={`/product/${product.pid}`}>  
                  <img src={"http://127.0.0.1:8000"+product.image} alt = "Product's Image" style={{height: 250, width:250}}></img>
                </Link>
                <Link className="nonActiveLink, productGridLink" to={`/product/${product.pid}`}>{product.name}</Link>
                <p>{product.price} â‚º</p>
              </div>

            </Grid>

          ))
          }

      </Grid>
        </div>
        );
                    
}


export default Cart;
