import React, {useState, useEffect} from 'react';
import { getUser, removeUserSession } from '../utils/Common';

import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Form from 'react-bootstrap/Form'
import Container from 'react-bootstrap/Container'
import axios from 'axios';
import Collapsible from 'react-collapsible';

function UserInfo(props) {
    const [user, setUser] = useState(props.user)
  const [loading, setLoading] = useState(false)
  const [error,setError] = useState(null)
  const [orders, setOrders] = useState([])
  const [result, setResult] = useState('');

  useEffect(() => {
    setResult(null);
    setLoading(true);
    
    axios({
        method: 'post',
        url: 'http://127.0.0.1:8000/api/usersorders/',
        data: {uid: user.uid},
    }).then(
        response => {
            setOrders(response.data);
            console.log(orders)
            setLoading(false);

        }).catch(
            error => {
                setLoading(false);
                if (error.response) {
                    console.log(error.response.status);
                    console.log(error.response.data.error);
                } else {
                    console.log('Error', error.message);
                }
            }
            )
  }, [])

    

    if(loading) {
        return (<p>Loading...</p>)
      }
    
    
    return(
    <div style={{textAlign: 'center', padding: 10, width: '100%'}}> 
    <ul>
      {orders.map((order, index) => {
        const SingleOrderRow = () => {
        var totalPrice = 0;
        var productCount = 0
        for (const ohp of order.connection) {
            totalPrice += ohp.orderedPrice
            productCount+=1
        }
        return (
            <div style={{height: 150,display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                <Container>
                <Row>
                    <Col xs={2}>
                        <Container>
                            <Row>
                            {
                                order.connection.map((ohp) => {
                                    return(
                                        <Col xs = {1}>
                                        <img src={"http://127.0.0.1:8000"+ohp.product.image} alt = "Product's Image" style={{height: 50, width:50}}></img>
                                        </Col>
                                    )
                                })
                            }
                            </Row>
                        </Container>
                    </Col>
                    <Col xs={2}>
                        <div>
                            <p>Order ID: {order.oid}</p><br />
                            <p>{order.date}</p>
                        </div>
                    </Col>
                    <Col xs={5}>{order.delivery}</Col>
                    <Col xs={2}>{totalPrice}$</Col>
                    <Col xs={1}>Collapse icon</Col>
                </Row>

                </Container>

            </div>
        )
        }  
          return(
            <div style={{border: '1px solid'}}>
            <Collapsible trigger={<SingleOrderRow />}>
                
                <ul>
                    {
                        order.connection.map((ohp) => {
                            return (
                                <li>{ohp.product.name}   {ohp.orderedPrice}$</li>
                            )
                        })
                    }
                </ul>

            </Collapsible>
            </div>
          )
      })}
      </ul>
    </div>
    )
    
  

}
export default UserInfo;
