        data: {caid:props.match.params.caid},
      }).then(
        response => {
          setProducts(response.data.products);
          setLoading(false);
          minPrice.change('')
              maxPrice.change('')
              setColor("all")
              setMaterial("all")
              order('old')
        }
        ).catch(
          error => {
            setLoading(false);
            if (error.response) {
                console.log(error.response.data);
                console.log(error.response.status);
                console.log(error.response.headers);
            } else {
                console.log('Error', error.message);
            }
          }
          )
      
    }
