          </div>
        </div>
      

        <header class = "Filter-Box-Header">Colors</header>
        
        <div class="Color-Filters">
        <fieldset id="group1" onChange={(e) => setColor(e.target.value)}>

          <div class="Color-Filter-3  Color-Filter">
          <input type="radio" class="Color-Filter-1" name="group1" value="ROSE" checked={color==="ROSE"}/>
          <label>Rose</label>
          </div>
          

          <div class="Color-Filter-4  Color-Filter">
          <input type="radio" class="Color-Filter-1" name="group1" value="SILVER" checked={color==="SILVER"}/>
          <label>Silver</label>
          </div>

          <div class="Color-Filter-5  Color-Filter">
          <input type="radio" class="Color-Filter-1" name="group1" value="GOLD" checked={color==="GOLD"}/>
          <label>Gold</label>
          </div>
        </fieldset>
        </div>
        

        <button  style={{marginRight: 5}} type="button" onClick = {(e) => {filter(e)}} class = "Filter-Button">Apply</button>
        <button type="button" onClick = {(e) => {resetFilters(e)}} class = "Filter-Button">Reset</button>
        </form>
    </div>
    
      
      <div className={classes.root}>
      <p class = "products-header">{category}</p><br></br>
      <div style={{width: '100%', height: 25}}>
        <Container>
          <Row className="Order-Row">
            <Col className="orderByButton">Order By:</Col>
            <Col onClick={() => {order('incPrc')}} className={selectedValue !== 'incPrc' ? "orderByButton" : "selectedOrderByButton"}>Increasing Price</Col>
            <Col onClick={() => {order('decPrc')}} className={selectedValue !== 'decPrc' ? "orderByButton" : "selectedOrderByButton"}>Decreasing Price</Col>
            <Col onClick={() => {order('new')}} className={selectedValue !== 'new' ? "orderByButton" : "selectedOrderByButton"}>Newest First</Col>
            <Col onClick={() => {order('old')}} className={selectedValue !== 'old' ? "orderByButton" : "selectedOrderByButton"}>Oldest First</Col>
            <Col onClick={() => {order('rating')}} className={selectedValue !== 'rating' ? "orderByButton" : "selectedOrderByButton"}>Most Rating</Col>
            
          </Row>
        </Container>
      </div>
      <Grid container spacing={10}>
          {
          products.map((product) => (

            <Grid item xs={3}>

              

                <Link to={`/product/${product.pid}`}>  
                  <img src={"http://127.0.0.1:8000"+product.image} alt = "Product's Image" style={{height: 250, width:250}}></img>
                </Link>
                <Link className="nonActiveLink, productGridLink" to={`/product/${product.pid}`}>{product.name}</Link>
                {product.price === product.discountPrice ? <p style={{fontWeight: 'bold', fontSize: 18, textAlign: 'right'}}>{product.price} ₺</p> : <div style={{display: 'flex', textAlign: 'right'}}><p style={{color: 'red', textDecoration: 'line-through', flex: 1, marginRight: -120}}>{product.price}$  </p><p style={{fontWeight: 'bold', fontSize: 18, flex: 1}}>{product.discountPrice}$</p></div>}
              

            </Grid>

          ))
          }

      </Grid>
      </div>
    </div>
  );
  }
}
const useFormInput = initialValue => {
  const [value, setValue] = useState(initialValue);
 
  const handleChange = e => {
    setValue(e.target.value);
  }
  return {
    value,
    onChange: handleChange,
    change: (val) => setValue(val) 
  }
}


