import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Products.css';
import Table from 'react-bootstrap/Table';
import { saveProducts } from '../utils/Common';
import { Link } from 'react-router-dom';
function AllProducts(props) {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [products, setProducts] = useState([]);
    const [caid, setCaid] = useState(-1)
    useEffect(() => {
        setLoading(true);
        setCaid(props.match.params.caid);
        axios({
            method: 'post',
            url: 'http://127.0.0.1:8000/api/productsbycategory/',
            data: {
              caid: props.match.params.caid
            }
          }).then(
            response => {
              //console.log(response.data)
              setProducts(response.data.products);
              console.log(response.data)
              saveProducts(response.data.products);
              setLoading(false);
            }
          ).catch(
            error => {
              setLoading(false);
              if (error.response.status === 401) setError(error.response.data.message);
              else setError("Something went wrong. Please try again later.");
            }
          )
          
    }, [])


    //console.log(products);
    //const productsArray = JSON.parse(products);
    
    if(loading) {
      return(<div>Loading...</div>)
    }
    else {
    return (

    <div class="Product-Category-Page-Container">

    <div class = "Filter-Box">

      <div>
        <input type="checkbox"/>
        <label>New</label>
      </div>

      <div>
        <input type="checkbox"/>
        <label>Used</label>
      </div>

      <div>
        <input type="checkbox"/>
        <label>Collectible</label>
      </div>

      <div>
        <input type="checkbox"/>
        <label>Renewed</label>
      </div>

      <div>
        <span>$0</span>
        <form>
          <input type="range" />
        </form>
        <span>$100</span>
      </div>

      <div class="btn-group btn-group-toggle btn-color-group d-block mt-n2 ml-n2" data-toggle="buttons">
        <label class="btn rounded-circle white border-inset-grey p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle grey p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle black p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle green p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle blue p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle purple p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle yellow p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle indigo p-3 m-2">
          <input type="checkbox" checked autocomplete="off"/>
        </label>
        <label class="btn rounded-circle red p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
        <label class="btn rounded-circle orange p-3 m-2">
          <input type="checkbox" autocomplete="off"/>
        </label>
      </div>

    </div>
      <div class = "All-Products-Grid">
          {
          products.map((product) => (
          
            <ul>

              <li><img src={"http://127.0.0.1:8000"+product.image} alt = "Product's Image"></img></li>
              <li><Link to={`/product/${product.pid}`}>{product.name}</Link></li>
              <li>{product.material}</li>
              <li>{product.color}</li>
              <li>{product.stock}</li>
              <li>{product.price}</li>
            
            </ul>
            
          ))
          }
      </div>
    </div>
  );
  }
}
 
export default AllProducts;
