// return the user data from the session storage
export const getUser = () => {
    const userStr = sessionStorage.getItem('user');
    if (userStr) return userStr; //user JSON.parser(userStr) but problematic for the now
    else return null;
  }
   
  // return the token from the session storage
  export const getRefreshToken = () => {
    return sessionStorage.getItem('refreshToken') || null;
  }

  export const getAccessToken = () => {
    return sessionStorage.getItem('accessToken') || null;
  }
   
  // remove the token and user from the session storage
  export const removeUserSession = () => {
    sessionStorage.removeItem('accessToken');
    sessionStorage.removeItem('user');
    sessionStorage.removeItem('refreshToken');
  }
   
  // set the token and user from the session storage
  export const setUserSession = (refresh, access, user) => {
    sessionStorage.setItem('accessToken', access);
    sessionStorage.setItem('refreshToken', refresh);
    sessionStorage.setItem('user', JSON.stringify(user));
  }

  export const saveProducts = (products) => {
    sessionStorage.setItem('products', JSON.stringify(products));
  }

  export const getProducts = () => {
    return JSON.parse(sessionStorage.getItem('products')) || null;
  }

  export const getProduct = (pid) => {
    const x = JSON.parse(sessionStorage.getItem('products'))
    var y = null;
    for(const val in x) {
      if(x[val].pid == pid) {
        y = x[val];
      }
    }
    return y || null;
  }
