import React, { useState, useEffect } from 'react';
import axios from 'axios';
import first_banner from '../assets/banner.jpg';

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


import { Link } from 'react-router-dom';
function Home() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [engagement, setEngagement] = useState([]);
  const [wedding, setWedding] = useState([]);
  
  useEffect(() => {
    setLoading(true);
    axios({
      method: 'get',
      url: 'http://127.0.0.1:8000/api/home/',
      data: {
      }
    }).then(
      response => {
        console.log(response.data)
        setEngagement(response.data.engagement)
        setWedding(response.data.wedding)
        console.log("wedding budur")
        console.log(wedding)
        console.log("engagement budur")
        console.log(engagement)
        setLoading(false);
      }
      ).catch(
        error => {
          setLoading(false);
          if (error.response.status === 401) setError(error.response.data.message);
          else setError("Something went wrong. Please try again later.");
        }
        )
        
      }, [])
      

      
      if (loading) {
        return (<div>Loading...</div>)
      }
      
      
      return (

        <div id="home-background">
        <div>
          <img src={first_banner} style={{width: '100%'}}></img>
        </div>
        <p class = "home-header">WEDDING RINGS TOP 8</p><br></br>
        <Container>
        <Row>

      
        
        {
          wedding.map((product) => 
          (
            
            <Col>
            
            <li><Link to={`/product/${product.pid}`} activeclassname="active">{product.name}</Link></li>
            <li>{product.material}</li>
            <li>{product.color}</li>
            <li>{product.stock}</li>
            <li>{product.price}</li>
            
            </Col>
          
            ))
          }
          </Row>
          </Container>
          <br></br><p class = "home-header">ENGAGEMENT RINGS TOP 8</p><br></br>
          {
            engagement.map((product) => (
              <>
              <div class = "col">
              
              <li><Link to={`/product/${product.pid}`} activeclassname="active">{product.name}</Link></li>
              <li>{product.material}</li>
              <li>{product.color}</li>
              <li>{product.stock}</li>
              <li>{product.price}</li>
              
              </div><br></br>
              </>
              ))
            }
            </div>
            );
          }
          
          export default Home;
