import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';


function Login(props) {
  const [loading, setLoading] = useState(false);
  //const username = useFormInput('');
  //const password = useFormInput('');
  const [query, setQuery] = useState('');
  const [error, setError] = useState(null);
  const [results, setResults] = useState([]);
 
  // handle button click of login form
  //post will change 
  //response may change
  const handleSearch = (val) => {
    setQuery(val);
    setError(null);
    setLoading(true);
   axios({
    method: 'post',
    url: 'http://127.0.0.1:8000/api/search/',
    data: {
      query: query,
    }
  }).then(
    response => {
      setLoading(false);
      setResults(response.data.results);
    }
  ).catch(
    error => {
      setLoading(false);
      if (error.response) {
        setError(error.response.status)
      }
      else setError(error.message);
    }
  )
  }
 
  return (
    <div>
      
      <div>
        Search<br />
        <input id="myInput" class = "Search-Box" enabled={!loading} type="text" value={query}  onSubmit={(event) => handleSearch(event.target.value)} />

      </div>
      {loading && <><small style={{ color: 'red' }}>{"Loading..."}</small><br /></>}<br />
      <div>
      <Grid container spacing={10}>
          {
          results.map((product) => (

            <Grid item xs={3}>

              <div class="Product-Single-Grid">

                <Link to={`/product/${product.pid}`}>  
                  <img src={"http://127.0.0.1:8000"+product.image} alt = "Product's Image" style={{height: 250, width:250}}></img>
                </Link>
                <Link className="nonActiveLink, productGridLink" to={`/product/${product.pid}`}>{product.name}</Link>
                <p>{product.price} ₺</p>
              </div>

            </Grid>

          ))
          }

      </Grid>
      </div>

    </div>
  );


}
 
const useFormInput = initialValue => {
  const [value, setValue] = useState(initialValue);
 
  const handleChange = e => {
    setValue(e.target.value);
  }
  return {
    value,
    onChange: handleChange
  }
}

export default Login;
